mapper.update:
update tblPlaylistComment
set content=#{content} 
where idx=#{idx}

choose.when:
SELECT * FROM vwPlaylist where isPublic=1         

    and (

        ${type} LIKE CONCAT('%', #{item}, '%')

    )


    and nick not in (

        #{item}

    )

 ORDER BY makeday DESC limit #{param2.curr},#{param2.cpp}

choose.otherwise:
SELECT * FROM vwPlaylist where isPublic=1 

and (

    ${type} LIKE CONCAT('%', #{item}, '%')

)


    and nick not in (

        #{item}

    )

ORDER BY likesCount DESC limit #{param2.curr},#{param2.cpp}

mapper.select:
select * from vwPlaylist
where nick in (select target from tblFollow where follow = #{nick})
order by likesCount desc;

mapper.update:
update tblSongs
set playlistID=#{playlistID}, title=#{title}, img=#{img}, songlength=#{songlength}, genre=#{genre}, tag=#{tag}, singer=#{singer}, songaddress=#{songaddress}, songorigin=#{songorigin}  
where idx=#{idx}
